function persistNewAccess(e){$.ajax({type:"POST",url:"/Home/PersistNewAccess",data:{ipAddress:e},error:function(){console.log("An error occurred during the ajax call to persist new access!")}})}window.onload=function(){$.getJSON("https://api.ipify.org?format=jsonp&callback=?",function(e){e.ip&&persistNewAccess(e.ip)})};